mapper.select:
select c.name,c.batchno ,c.bankname,c.cardno,m.imptime as inputtime,m.inmoney as backbalance,c.ownerid
from case_info c
right join back_money m on m.cid=c.guid
LEFT JOIN case_contacts b ON c.GUID = b.CID
where 1=1   and   m.cid=c.guid

    and c.ownerid = #{ownerid}


    and c.batchno = #{batchno}


    and c.bankname = #{bankname}

select c.guid,m.name,c.batchno ,c.bankname,c.cardno,c.certno,c.addr,c.mobile,c.balance,m.imptime as inputtime,m.inmoney as backbalance,c.ownerid from case_info c right join back_money m on m.cid=c.guid where 1=1 and m.cid=c.guid
and c.ownerid = #{ownerid}
and c.batchno = #{batchno}
and m.name LIKE CONCAT('%',#{bankname},'%')

SELECT COUNT(*) FROM case_info c RIGHT JOIN back_money m ON m.cid=c.guid WHERE 1=1 AND m.cid=c.guid
and c.ownerid = #{ownerid}
and c.batchno = #{batchno}
and m.name LIKE CONCAT('%',#{bankname},'%')

select count(c.name) from case_info c
right join back_money m on m.cid=c.guid   LEFT JOIN case_contacts b ON c.GUID = b.CID
where 1=1  and   m.cid=c.guid


    and c.ownerid = #{ownerid}


    and c.batchno = #{batchno}


    and c.bankname = #{bankname}

select c.name,c.batchno ,c.bankname,c.cardno,m.imptime as inputtime,m.inmoney as backbalance,c.ownerid from case_info c right join back_money m on m.cid=c.guid LEFT JOIN case_contacts b ON c.GUID = b.CID where 1=1 and m.cid=c.guid
AND CONCAT_WS( IFNULL(c.ADDR,'无'), IFNULL(c.`NAME`,'无'), IFNULL(c.MOBILE,'无'), IFNULL(c.DEPTNAME,'无'), IFNULL(c.HOMEADDR,'无'), IFNULL(c.CERTNO,'无'), IFNULL(c.CARDNO,'无'), IFNULL(b.CELLPHONE,'无'), IFNULL(b.`NAME`,'无'), IFNULL(b.REMARK,'无') ) LIKE CONCAT('%',#{keyWord},'%')
and c.ownerid = #{ownerid}
and c.batchno = #{batchno}
and c.bankname = #{bankname}

select count(c.name) from case_info c right join back_money m on m.cid=c.guid LEFT JOIN case_contacts b ON c.GUID = b.CID where bankname = #{bankname} and m.cid=c.guid AND CONCAT_WS( IFNULL(c.ADDR,'无'), IFNULL(c.`NAME`,'无'), IFNULL(c.MOBILE,'无'), IFNULL(c.DEPTNAME,'无'), IFNULL(c.HOMEADDR,'无'), IFNULL(c.CERTNO,'无'), IFNULL(c.CARDNO,'无'), IFNULL(b.CELLPHONE,'无'), IFNULL(b.`NAME`,'无'), IFNULL(b.REMARK,'无') ) LIKE CONCAT('%',#{keyWord},'%')
and c.ownerid = #{ownerid}
and c.batchno = #{batchno}
and c.bankname = #{bankname}
